import React, { useState } from 'react';
// import Select from 'react-select';
import 'bootstrap/dist/css/bootstrap.min.css';

const UpdateDetails = ({ task, onUpdate }) => {
    const [title, setTitle] = useState(task.title || ''); // Initialize state with task title
    const [content, setContent] = useState(task.content || ''); // Initialize state with task content
    // const [selectedTags, setSelectedTags] = useState([]);

    // const options = [
    //     { value: 'sunday', label: 'Sunday' },
    //     { value: 'monday', label: 'Monday' },
    //     { value: 'tuesday', label: 'Tuesday' },
    //     { value: 'wednesday', label: 'Wednesday' },
    //     { value: 'thursday', label: 'Thursday' },
    //     { value: 'friday', label: 'Friday' },
    //     { value: 'saturday', label: 'Saturday' }
    // ];


    // const handleTagChange = (selectedOptions) => {
    //     setSelectedTags(selectedOptions); 
    // };

    const handleSubmit = async (e) => {
        e.preventDefault(); 

        const updatedTask = { 
            ...task, title, content, 
            // ...task, title, content, selectedTags 
        }; 

        try {
            const response = await fetch(`/api/tasks/update/${task._id}`, {
                method: 'PATCH', 
                body: JSON.stringify(updatedTask),
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            if (response.ok) {
                console.log('Task updated successfully!');
                console.log(updatedTask);
                // onUpdate(updatedTask); // Update task in parent component
            } else {
                console.error('Failed to update task');
            }
        } catch (error) {
            console.error('Error updating task:', error);
        }
    };

    return (
        <form className="card p-3 mt-4" onSubmit={handleSubmit}>
            <h3>Edit Task</h3>
                <div className=''>
                    <div className='form-floating'>
                        <input
                            className='form-control'
                            id="content-input"
                            type="text"
                            onChange={(e) => setTitle(e.target.value)}
                            value={title}
                            required
                        />
                        <label>Title</label>
                    </div>
                    {/* <div className='col-12'>
                        <label>Days:</label>
                        <Select
                            options={options}
                            isMulti
                            onChange={handleTagChange}
                        />
                    </div> */}
                    <div className='form-floating'>
                        <textarea
                    className='form-control'
                    id="content-input"
                            type="text"
                            onChange={(e) => setContent(e.target.value)}
                            required
                        >{content}</textarea>
                        <label>Content</label>
                    </div>
                        <div className="button">
                            <button >Save</button>
                        </div>
                    
                </div>
        </form>
    );
};

export default UpdateDetails;
