import React, { useState } from 'react';
import Select from 'react-select';

const UpdateDetails = ({ task, onUpdate }) => {
    const [title, setTitle] = useState(task.title || '');
    const [content, setContent] = useState(task.content || '');
    const [selectedTags, setSelectedTags] = useState([]);
    const [updatedSelectedTags, setUpdatedSelectedTags] = useState([]); // New state variable for updated selected tags

    const options = [
        { value: 'sunday', label: 'Sunday' },
        { value: 'monday', label: 'Monday' },
        { value: 'tuesday', label: 'Tuesday' },
        { value: 'wednesday', label: 'Wednesday' },
        { value: 'thursday', label: 'Thursday' },
        { value: 'friday', label: 'Friday' },
        { value: 'saturday', label: 'Saturday' }
    ];

    // Function to handle tag change
    const handleTagChange = (selectedOptions) => {
        setSelectedTags(selectedOptions); // Update selectedTags state
        setUpdatedSelectedTags(selectedOptions.map(option => option.value)); // Update updatedSelectedTags state
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const updatedTask = { ...task, title, content, selectedTags: updatedSelectedTags }; // Use updated selected tags

        try {
            const response = await fetch(`/api/tasks/update/${task._id}`, {
                method: 'PATCH',
                body: JSON.stringify(updatedTask),
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            if (response.ok) {
                console.log('Task updated successfully!');
                onUpdate(updatedTask); // Update task in parent component
            } else {
                console.error('Failed to update task');
            }
        } catch (error) {
            console.error('Error updating task:', error);
        }
    };

    return (
        <form className="update" onSubmit={handleSubmit}>
            <div>
                <h3>Edit Task</h3>

                <label>Title</label>
                <input
                    type="text"
                    onChange={(e) => setTitle(e.target.value)}
                    value={title}
                    required
                />

                <label>Days:</label>
                <Select
                    options={options}
                    isMulti
                    value={selectedTags}
                    onChange={handleTagChange}
                />

                <label>Content</label>
                <textarea
                    type="text"
                    onChange={(e) => setContent(e.target.value)}
                    required
                >{content}</textarea>

                <div className="button">
                    <button type="submit">Save</button>
                </div>
            </div>
        </form>
    );
};

export default UpdateDetails;
