import React, { useState } from 'react';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'

const UpdateDetails = ({ task, onUpdate }) => {
    const [title, setTitle] = useState(task.title || '');
    const [content, setContent] = useState(task.content || '');
    const [showModal, setShowModal] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault(); 
    
        const updatedTask = { ...task, title, content };
    
        try {
            const response = await fetch(`/api/tasks/update/${task._id}`, {
                method: 'PATCH',
                body: JSON.stringify(updatedTask),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
    
            if (response.ok) {
                console.log('Task updated successfully!');
                setShowModal(false);
                // console.log(updatedTask);

            } else {
                console.error('Failed to update task');
            }
        } catch (error) {
            console.error('Error updating task:', error);
        }
    };

    const handleSave = () => {
        Swal.fire({
            title: 'Changes have been saved!',
            icon: 'success',
            toast: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 3000
        });
    };    
    
    return (
        <>
            <button className='btn btn-sm btn-success' onClick={() => setShowModal(true)}>Edit</button>

            <div className='modal' tabIndex='-1' style={{ display: showModal ? 'block' : 'none' }}>
                <div className='modal-dialog modal-dialog-centered'>
                    <div className='modal-content'>
                        <div className='modal-header'>
                            <h5 className='modal-title'>Edit Task</h5>
                            <button type='button' className='btn-close' onClick={() => setShowModal(false)}></button>
                        </div>
                        <div className='modal-body'>
                            <form onSubmit={handleSubmit}>
                                <div className='form-floating'>
                                    <input
                                        className='form-control'
                                        id='title-input'
                                        type='text'
                                        onChange={(e) => setTitle(e.target.value)}
                                        value={title}
                                        required
                                    />
                                    <label htmlFor='title-input'>Title</label>
                                </div>
                                <div className='form-floating mt-2'>
                                    <textarea
                                        className='form-control'
                                        id='content-input'
                                        type='text'
                                        onChange={(e) => setContent(e.target.value)}
                                        rows={6}
                                        required
                                        value={content}
                                    ></textarea>
                                    <label htmlFor='content-input'>Content</label>
                                </div>
                            </form>
                        </div>
                        <div className='modal-footer'>
                            <button className='btn btn-sm btn-success' onClick={handleSubmit}>Save</button>
                            <button type='button' className='btn btn-sm btn-secondary' onClick={() => setShowModal(false)}>Close</button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default UpdateDetails;
